        curr |= (std::to_underlying(VCOTR) & 0b1u) << 2u;
        curr &= ~(0b1u << 1u);
        curr |= (std::to_underlying(VCOPWD) & 0b1u) << 1u;
        curr &= ~(0b1u << 0u);
        curr |= (std::to_underlying(VCOBYP) & 0b1u) << 0u;

